async function dogPoundPerformance(App, prices) {
  _print("");
  _print("------------------------------------------------------------------")
  _print_bold("Dog Pound Performance");
  _print("\nThe Auto Compounding Pool allows you to earn your Rewards in PIGS.");
  _print("The BNB rewards are automatically converted to a AFD/WBNB LP");
  _print("and used in the core farm AFD/WBNB LP where you can claim the PIGS.\n");
  _print("Please visit the original site for more information.\n");
  const dpContract = new ethers.Contract("0x6da8227bc7b576781ffcac69437e17b8d4f4ae41", DOG_POUND_ABI, App.provider);
  const autoContractAddress = await dpContract.DogPoundAutoPool();
  const linearContractAddress = await dpContract.DogPoundLinearPool();
  const autoContract = new ethers.Contract(autoContractAddress, AUTO_ABI, App.provider);
  const linearContract = new ethers.Contract(linearContractAddress, LINEAR_ABI, App.provider);

  const stakeTokenAddress = await dpContract.DogsToken();
  const stakeToken = await getBscToken(App, stakeTokenAddress, autoContractAddress);
  const autoTotalstaked = await dpContract.autoPoolSize() / 10 ** stakeToken.decimals;
  const linearTotalstaked = await dpContract.linearPoolSize() / 10 ** stakeToken.decimals;

  const stakeTokenPrice = getParameterCaseInsensitive(prices, stakeTokenAddress)?.usd;
  const totalUsdAutoStaked = autoTotalstaked * stakeTokenPrice;
  const totalUsdLinearStaked = linearTotalstaked * stakeTokenPrice;

  const autoRewardToken1Address = await autoContract.Dogs_BNB_LpToken();
  const autoRewardToken2Address = await autoContract.PigsToken();
  const autoRewardToken1 = await getBscToken(App, autoRewardToken1Address, autoContractAddress);
  const autoRewardToken2 = await getBscToken(App, autoRewardToken2Address, autoContractAddress);
  const _userAutoStaked = await autoContract.userInfo(App.YOUR_ADDRESS);
  const userAutoStaked = _userAutoStaked.amount / 10 ** stakeToken.decimals;
  const userAutoStakedUsd = userAutoStaked * stakeTokenPrice;
  const pendingAutoPigs = await autoContract.pendingPigsRewards(App.YOUR_ADDRESS) / 10 ** autoRewardToken2.decimals;
  const pendingAutoLps = await autoContract.pendingLpRewards(App.YOUR_ADDRESS) / 10 ** autoRewardToken1.decimals;

  const linearRewardTokenAddress = "0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c";  //WBNB
  const linearRewardToken = await getBscToken(App, linearRewardTokenAddress, linearContractAddress);
  const _userLinearStaked = await linearContract.userInfo(App.YOUR_ADDRESS);
  const userLinearStaked = _userLinearStaked.totalStaked / 10 ** stakeToken.decimals;
  const userLinearStakedUsd = userLinearStaked * stakeTokenPrice;
  const PendingLinearBnb = await linearContract.pendingBNBReward(App.YOUR_ADDRESS) / 10 ** linearRewardToken.decimals;

  _print_bold("Stake (Auto-Compounding) Pool")
  _print("------------------------------------------------")
  _print(`Total Dogs Staked : ${autoTotalstaked.toFixed(2)} $(${formatMoney(totalUsdAutoStaked)})`);
  _print(`You are staking : ${userAutoStaked.toFixed(2)} $(${formatMoney(userAutoStakedUsd)})`);
  _print(`Pending PIGS Earned : ${pendingAutoPigs.toFixed(2)}`);
  _print(`Pending LP (AFD/WBNB) Earned : ${pendingAutoLps.toFixed(2)}`);

  _print("")

  _print_bold("Stake (Linear) Pool")
  _print("------------------------------------------------")
  _print(`Total Dogs Staked : ${linearTotalstaked.toFixed(2)} $(${formatMoney(totalUsdLinearStaked)})`);
  _print(`You are staking : ${userLinearStaked.toFixed(2)} $(${formatMoney(userLinearStakedUsd)})`);
  _print(`Pending BNB Earned : ${PendingLinearBnb.toFixed(2)}`);
}

async function getAnimalsPoolInfo(App, chefContract, chefAddress, poolIndex, pendingRewardsFunction, clicked) {
  if(!clicked){
    const poolInfo = await chefContract.poolInfo(poolIndex);
    if (poolInfo.allocPoint == 0 || poolIndex == 105) {
      return {
        address: poolInfo.lpToken ?? poolInfo.token,
        allocPoints: poolInfo.allocPoint ?? 1,
        poolToken: null,
        userStaked : 0,
        pendingRewardTokens : 0,
        stakedToken : null,
        userLPStaked : 0,
        lastRewardBlock : poolInfo.lastRewardBlock
      };
    }
    const poolToken = await getBscToken(App, poolInfo.lpToken ?? poolInfo.token, chefAddress);
    poolToken.staked = poolInfo.lpSupply / 10 ** poolToken.decimals;
    const userInfo = await chefContract.userInfo(poolIndex, App.YOUR_ADDRESS);
    const pendingRewardTokens = await chefContract.callStatic[pendingRewardsFunction](poolIndex, App.YOUR_ADDRESS);
    const staked = userInfo.amount / 10 ** poolToken.decimals;
    return {
        address: poolInfo.lpToken ?? poolInfo.token,
        allocPoints: poolInfo.allocPoint ?? 1,
        poolToken: poolToken,
        userStaked : staked,
        pendingRewardTokens : pendingRewardTokens / 10 ** 18,
        depositFee : (poolInfo.depositFeeBP ?? 0) / 100,
        withdrawFee : (poolInfo.withdrawFeeBP ?? 0) / 100
    };
  }else{
    const poolInfo = await chefContract.poolInfo(poolIndex);
    const poolToken = await getBscToken(App, poolInfo.lpToken ?? poolInfo.token, chefAddress);
    poolToken.staked = poolInfo.lpSupply / 10 ** poolToken.decimals;
    const userInfo = await chefContract.userInfo(poolIndex, App.YOUR_ADDRESS);
    const pendingRewardTokens = await chefContract.callStatic[pendingRewardsFunction](poolIndex, App.YOUR_ADDRESS);
    const staked = userInfo.amount / 10 ** poolToken.decimals;
    if (staked <= 0) {
      return {
        address: poolInfo.lpToken ?? poolInfo.token,
        allocPoints: poolInfo.allocPoint ?? 1,
        poolToken: null,
        userStaked : 0,
        pendingRewardTokens : 0,
        stakedToken : null,
        userLPStaked : 0,
        lastRewardBlock : poolInfo.lastRewardBlock
      };
    }
    return {
        address: poolInfo.lpToken ?? poolInfo.token,
        allocPoints: poolInfo.allocPoint ?? 1,
        poolToken: poolToken,
        userStaked : staked,
        pendingRewardTokens : pendingRewardTokens / 10 ** 18,
        depositFee : (poolInfo.depositFeeBP ?? 0) / 100,
        withdrawFee : (poolInfo.withdrawFeeBP ?? 0) / 100
    };
  }
}

async function loadAnimalsContract(App, tokens, prices, chef, chefAddress, chefAbi, rewardTokenTicker,
  rewardTokenFunction, rewardsPerBlockFunction, rewardsPerWeekFixed, pendingRewardsFunction,
  deathPoolIndices, clicked) {
  const chefContract = chef ?? new ethers.Contract(chefAddress, chefAbi, App.provider);

  const poolCount = parseInt(await chefContract.poolLength(), 10);
  const totalAllocPoints = await chefContract.totalAllocPoint();

  if(!clicked){
    _print(`<a href='https://bscscan.com/address/${chefAddress}' target='_blank'>Staking Contract</a>`);
    _print(`Found ${poolCount} pools.\n`)

    _print(`Showing incentivized pools only.\n`);
  }else{
    _print(`<a href='https://bscscan.com/address/${chefAddress}' target='_blank'>Staking Contract</a>`);
    _print(`Found ${poolCount} pools.\n`)

    _print(`Showing your staking pools only\n`);
  }

  var tokens = {};

  const rewardTokenAddress = await chefContract.callStatic[rewardTokenFunction]();
  const rewardToken = await getBscToken(App, rewardTokenAddress, chefAddress);
  const rewardsPerWeek = rewardsPerWeekFixed ??
    await chefContract.callStatic[rewardsPerBlockFunction]()
    / 10 ** rewardToken.decimals * 604800 / 3

  const poolInfos = await Promise.all([...Array(poolCount).keys()].map(async (x) =>
    await getAnimalsPoolInfo(App, chefContract, chefAddress, x, pendingRewardsFunction, clicked)));

  var tokenAddresses = [].concat.apply([], poolInfos.filter(x => x.poolToken).map(x => x.poolToken.tokens));

  await Promise.all(tokenAddresses.map(async (address) => {
      tokens[address] = await getBscToken(App, address, chefAddress);
  }));

  if (deathPoolIndices) {   //load prices for the deathpool assets
    deathPoolIndices.map(i => poolInfos[i])
                     .map(poolInfo =>
      poolInfo.poolToken ? getPoolPrices(tokens, prices, poolInfo.poolToken, "bsc") : undefined);
  }

  const poolPrices = poolInfos.map(poolInfo => poolInfo.poolToken ? getPoolPrices(tokens, prices, poolInfo.poolToken, "bsc") : undefined);


  _print("Finished reading smart contracts.\n");

  let aprs = []
  for (i = 0; i < poolCount; i++) {
    if (poolPrices[i]) {
      const apr = printChefPool(App, chefAbi, chefAddress, prices, tokens, poolInfos[i], i, poolPrices[i],
        totalAllocPoints, rewardsPerWeek, rewardTokenTicker, rewardTokenAddress,
        pendingRewardsFunction, null, null, "bsc", poolInfos[i].depositFee, poolInfos[i].withdrawFee)
      aprs.push(apr);
    }
  }
  let totalUserStaked=0, totalStaked=0, averageApr=0;
  for (const a of aprs) {
    if (!isNaN(a.totalStakedUsd)) {
      totalStaked += a.totalStakedUsd;
    }
    if (a.userStakedUsd > 0) {
      totalUserStaked += a.userStakedUsd;
      averageApr += a.userStakedUsd * a.yearlyAPR / 100;
    }
  }
  averageApr = averageApr / totalUserStaked;
  if(!clicked){
    _print_bold(`Total Staked: $${formatMoney(totalStaked)}`);
  }
  if (totalUserStaked > 0) {
    _print_bold(`\nYou are staking a total of $${formatMoney(totalUserStaked)} at an average APR of ${(averageApr * 100).toFixed(2)}%`)
    _print(`Estimated earnings:`
        + ` Day $${formatMoney(totalUserStaked*averageApr/365)}`
        + ` Week $${formatMoney(totalUserStaked*averageApr/52)}`
        + ` Year $${formatMoney(totalUserStaked*averageApr)}\n`);
  }
  return { prices, totalUserStaked, totalStaked, averageApr }
}

async function loadAnimalsSynthetixPoolInfo(App, tokens, prices, stakeTokenAddress) {
  var stakeToken = await getBscToken(App, stakeTokenAddress, stakeTokenAddress);
  var newPriceAddresses = stakeToken.tokens.filter(x =>
    !getParameterCaseInsensitive(prices, x));
  var newPrices = await lookUpTokenPrices(newPriceAddresses);
  for (const key in newPrices) {
    if (newPrices[key]?.usd)
        prices[key] = newPrices[key];
  }
  var newTokenAddresses = stakeToken.tokens.filter(x =>
    !getParameterCaseInsensitive(tokens,x));
  for (const address of newTokenAddresses) {
      tokens[address] = await getBscToken(App, address, stakeTokenAddress);
  }
  const poolPrices = getPoolPrices(tokens, prices, stakeToken, "bsc");
  if (!poolPrices)
  {
    console.log(`Couldn't calculate prices for pool ${stakeTokenAddress}`);
    return null;
  }
  const stakeTokenTicker = poolPrices.stakeTokenTicker;

  const stakeTokenPrice =
      prices[stakeTokenAddress]?.usd ?? getParameterCaseInsensitive(prices, stakeTokenAddress)?.usd;
  return  {
    poolPrices,
    stakeTokenAddress,
    stakeTokenTicker,
    stakeTokenPrice
  }
}